={{ (() => {
  // 0) Read model output from AI Agent node
  const raw = String(($('AI Agent Data and Score').item.json.output ?? ''));

  // 1) Strip code fences
  let s = raw
    .replace(/`{2,3}(?:json)?\s*/gi, '')
    .replace(/\s*`{3}\s*$/gi, '');

  // 1b) Normalize curly quotes (Gemini sometimes emits them)
  s = s.replace(/[\u201C\u201D]/g, '"').replace(/\u2019/g, "'");

  // 2) Cut at END_OF_JSON if present
  const endIdx = s.indexOf('END_OF_JSON');
  if (endIdx !== -1) s = s.slice(0, endIdx);

  // 3) Extract first complete { ... } by brace counting
  const start = s.indexOf('{');
  if (start < 0) return {};
  let depth = 0, inStr = false, esc = false, end = -1;
  for (let i = start; i < s.length; i++) {
    const ch = s[i];
    if (inStr) {
      if (esc) { esc = false; }
      else if (ch === '\\') { esc = true; }
      else if (ch === '"') { inStr = false; }
    } else {
      if (ch === '"') inStr = true;
      else if (ch === '{') depth++;
      else if (ch === '}') { depth--; if (depth === 0) { end = i + 1; break; } }
    }
  }
  if (end < 0) return {};

  const cleaned = s.slice(start, end).trim();

  try {
    return JSON.parse(cleaned);   // JSON mode expects an OBJECT
  } catch (e) {
    // As a last resort, return an empty object to keep the run alive
    return {};
  }
})() }}

